    /* Base button styles */
    .contact-buttons .btn {
      display: inline-flex;
      align-items: center;
      justify-content: center;
      gap: 8px;
      padding: 14px 24px;
      font-weight: 400;
      font-size: 14px;
      text-decoration: none;
      border: none;
      cursor: pointer;
      transition: all 0.2s ease;
      min-width: 140px;
      text-align: center;
      line-height: 1.2;
    }
    
    /* Contact button - muted green, rectangular */
    .contact-buttons .btn-contact {
       background-color: #6dab3c;
    border-color: #6dab3c;
    color: #fff;
    border-radius: 25px;
    padding: 14px 30px;
      text-transform: uppercase;
      letter-spacing: 0.5px;
    }
    
    .contact-buttons .btn-contact:hover {
      background: #689F38;
      transform: translateY(-1px);
    }
    
    /* WhatsApp button - bright green, rounded */
    .contact-buttons .btn-whatsapp {
      background: var(--whatsapp-green);
      color: var(--white);
      border-radius: 25px;
      padding: 14px 28px;
    }
    
    .contact-buttons .btn-whatsapp:hover {
      background: #128C7E;
      transform: translateY(-1px);
    }
    
    /* WhatsApp icon */
    .contact-buttons .whatsapp-icon {
      width: 20px;
      height: 20px;
      fill: currentColor;
    }
    
    /* Responsive design */
    @media (max-width: 480px) {
      .contact-buttons {
        flex-direction: column;
        gap: 12px;
      }
      
      .contact-buttons .btn {
        width: 100%;
        max-width: 280px;
        min-width: auto;
      }
    }
    
    /* Reduced motion */
    @media (prefers-reduced-motion: reduce) {
      .contact-buttons .btn {
        transition: none;
      }
      
      .contact-buttons .btn:hover {
        transform: none;
      }
    }
