    <script>
        (function () {
            const root = document.getElementById('vpb-root-73b2');
            if (!root) return;

            const modal = root.querySelector('.vpb73b2-modal');
            const embedContainer = root.querySelector('.vpb73b2-embed');
            const iframe = root.querySelector('.vpb73b2-embed iframe');
            const closeEls = root.querySelectorAll('[data-close]');
            let lastTrigger = null;

            function getYouTubeId(input) {
                if (!input) return null;
                const str = String(input).trim();

                // Already a plain 11-char ID
                const plain = str.match(/^[a-zA-Z0-9_-]{11}$/);
                if (plain) return plain[0];

                // Try URL parsing
                try {
                    const u = new URL(str);

                    // youtu.be/<id>?t=30
                    if (u.hostname.includes('youtu.be')) {
                        const id = u.pathname.split('/').filter(Boolean)[0];
                        return id ? id.substring(0, 11) : null;
                    }

                    // watch?v=<id>&t=30s
                    const v = u.searchParams.get('v');
                    if (v) return v.substring(0, 11);

                    // /shorts/<id>
                    if (u.pathname.startsWith('/shorts/')) {
                        const parts = u.pathname.split('/').filter(Boolean);
                        const id = parts[1] || parts[0];
                        return id ? id.substring(0, 11) : null;
                    }

                    // /embed/<id>
                    const parts = u.pathname.split('/').filter(Boolean);
                    const embedIdx = parts.indexOf('embed');
                    if (embedIdx > -1 && parts[embedIdx + 1]) {
                        return parts[embedIdx + 1].substring(0, 11);
                    }
                } catch (e) {
                    // Not a URL; fall through
                }

                // Fallback: pick ID from common patterns with extra params
                const m = str.match(/(?:youtu\.be\/|v=|embed\/|shorts\/)([a-zA-Z0-9_-]{11})/);
                return m ? m[1] : null;
            }

            function openModal(videoUrl, isMP4 = false) {
                if (!modal || !embedContainer) return;
                
                // Clear previous content
                embedContainer.innerHTML = '';
                
                if (isMP4) {
                    // Create video element for MP4
                    const video = document.createElement('video');
                    video.setAttribute('controls', 'true');
                    video.setAttribute('autoplay', 'true');
                    video.setAttribute('playsinline', 'true');
                    video.style.width = '100%';
                    video.style.height = '100%';
                    
                    const source = document.createElement('source');
                    source.setAttribute('src', videoUrl);
                    source.setAttribute('type', 'video/mp4');
                    
                    video.appendChild(source);
                    embedContainer.appendChild(video);
                } else {
                    // Create iframe for YouTube
                    const iframe = document.createElement('iframe');
                    iframe.setAttribute('src', 'https://www.youtube.com/embed/' + videoUrl + '?autoplay=1&rel=0&modestbranding=1');
                    iframe.setAttribute('allow', 'autoplay; fullscreen');
                    iframe.setAttribute('allowfullscreen', 'true');
                    iframe.setAttribute('title', 'Video player');
                    iframe.style.width = '100%';
                    iframe.style.height = '100%';
                    iframe.style.border = 'none';
                    
                    embedContainer.appendChild(iframe);
                }
                
                modal.classList.add('vpb73b2-open');
                modal.setAttribute('aria-hidden', 'false');
                // Focus close button
                const closeBtn = modal.querySelector('.vpb73b2-close');
                closeBtn && closeBtn.focus();
                document.addEventListener('keydown', escHandler);
            }

            function closeModal() {
                if (!modal || !embedContainer) return;
                embedContainer.innerHTML = ''; // Clear content to stop playback
                modal.classList.remove('vpb73b2-open');
                modal.setAttribute('aria-hidden', 'true');
                document.removeEventListener('keydown', escHandler);
                if (lastTrigger && typeof lastTrigger.focus === 'function') {
                    lastTrigger.focus();
                }
            }

            function escHandler(e) {
                if (e.key === 'Escape') closeModal();
            }

            root.querySelectorAll('.vpb73b2-card').forEach((btn) => {
                btn.addEventListener('click', () => {
                    lastTrigger = btn;
                    const raw = btn.getAttribute('data-video');
                    const isMP4 = btn.hasAttribute('data-type') && btn.getAttribute('data-type') === 'mp4';
                    
                    if (isMP4) {
                        openModal(raw, true);
                    } else {
                        const id = getYouTubeId(raw);
                        openModal(id, false);
                    }
                });
            });

            closeEls.forEach((el) => el.addEventListener('click', closeModal));
        })();
    </script>
